import React, { useState } from "react";
import "./App.css";
import TorontoData from "./Toronto.json";

//.filter(r => r.TEXT_NAME_NOM.includes("Korean"));

//0: mother tongue
//1: spoken at home
//2: total speakers of this language (including those not included in 0 & 1)

function App() {
  const [selectedLanguage, setLanguage] = useState("");
  return (
    <div className="App">
      <h1>This thing is very rude</h1>
      <input
        onChange={e => setLanguage(e.target.value.toLowerCase())}
        placeholder="type language"
      ></input>
      <div>
        Number:{" "}
        {TorontoData.filter(d =>
          d.TEXT_NAME_NOM.toLowerCase().includes(selectedLanguage)
        )
          .map(d => d.T_DATA_DONNEE)
          .reduce((a, n) => a + n, 0)}
      </div>
    </div>
  );
}

export default App;
